function updateCharCount(){const textarea=document.getElementById("comment"),charCountSpan=document.getElementById("charCount");textarea&&charCountSpan&&(charCountSpan.textContent=textarea.value.length)}function updateShortDescCount(){const shortDescInput=document.getElementById("short"),charCountSpan=document.getElementById("shortDescCount");shortDescInput&&charCountSpan&&(charCountSpan.textContent=`${shortDescInput.value.length}/80`)}function updateDescriptionCount(){const descriptionTextarea=document.getElementById("description"),charCountSpan=document.getElementById("descriptionCount");descriptionTextarea&&charCountSpan&&(charCountSpan.textContent=descriptionTextarea.value.length)}function convertTurkishChars(text){const turkishMap={"ç":"c","ğ":"g","ı":"i","ö":"o","ş":"s","ü":"u","Ç":"C","Ğ":"G","İ":"I","Ö":"O","Ş":"S","Ü":"U"};return text.replace(/[\u00C0-\u017F]/g,(function(c){return turkishMap[c]||c}))}function createSlug(text){return convertTurkishChars(text).toLowerCase().trim().replace(/[^a-z0-9\s-]/g,"").replace(/\s+/g,"-").replace(/-+/g,"-")}function updateSlugField(){const slugInput=document.getElementById("slug"),sourceInput=document.getElementById("category");sourceInput&&slugInput&&(slugInput.value=createSlug(sourceInput.value))}window.onload=function(){const textarea=document.getElementById("review");textarea&&textarea.addEventListener("input",updateCharCount);const shortDescInput=document.getElementById("short");shortDescInput&&shortDescInput.addEventListener("input",updateShortDescCount);const descriptionTextarea=document.getElementById("description");descriptionTextarea&&descriptionTextarea.addEventListener("input",updateDescriptionCount);const sectorSelect=document.getElementById("sector");sectorSelect&&sectorSelect.addEventListener("change",handleSectorChange);const sourceInput=document.getElementById("category");sourceInput&&sourceInput.addEventListener("input",updateSlugField)};