function updateCharCount(){const textarea=document.getElementById("review"),charCountSpan=document.getElementById("charCount");textarea&&charCountSpan&&(charCountSpan.textContent=textarea.value.length)}function updateShortDescCount(){const shortDescInput=document.getElementById("short"),charCountSpan=document.getElementById("shortDescCount");shortDescInput&&charCountSpan&&(charCountSpan.textContent=`${shortDescInput.value.length}/80`)}function updateDescriptionCount(){const descriptionTextarea=document.getElementById("description"),charCountSpan=document.getElementById("descriptionCount");descriptionTextarea&&charCountSpan&&(charCountSpan.textContent=descriptionTextarea.value.length)}function handleSectorChange(){const sectorSelect=document.getElementById("sector"),categorySelect=document.getElementById("category");sectorSelect&&categorySelect&&("null"!==sectorSelect.value?(categorySelect.disabled=!1,categorySelect.innerHTML='\n                <option value="null">Select</option>\n                <option value="cat1">Category 1</option>\n                <option value="cat2">Category 2</option>\n                <option value="cat3">Category 3</option>\n            '):(categorySelect.disabled=!0,categorySelect.innerHTML='<option value="null">Select</option>'))}function convertTurkishChars(text){const turkishMap={"ç":"c","ğ":"g","ı":"i","ö":"o","ş":"s","ü":"u","Ç":"C","Ğ":"G","İ":"I","Ö":"O","Ş":"S","Ü":"U"};return text.replace(/[\u00C0-\u017F]/g,(function(c){return turkishMap[c]||c}))}function createSlug(text){return convertTurkishChars(text).toLowerCase().trim().replace(/[^a-z0-9\s-]/g,"").replace(/\s+/g,"-").replace(/-+/g,"-")}function updateSlugField(){const slugInput=document.getElementById("slug"),sourceInput=document.getElementById("category");sourceInput&&slugInput&&(slugInput.value=createSlug(sourceInput.value))}document.addEventListener("DOMContentLoaded",(function(){var inputField=document.getElementById("productInput");inputField.addEventListener("focus",(function(){setTimeout((()=>{inputField.select()}),50)}))})),window.onload=function(){const textarea=document.getElementById("review");textarea&&textarea.addEventListener("input",updateCharCount);const shortDescInput=document.getElementById("short");shortDescInput&&shortDescInput.addEventListener("input",updateShortDescCount);const descriptionTextarea=document.getElementById("description");descriptionTextarea&&descriptionTextarea.addEventListener("input",updateDescriptionCount);const sectorSelect=document.getElementById("sector");sectorSelect&&sectorSelect.addEventListener("change",handleSectorChange);const sourceInput=document.getElementById("category");sourceInput&&sourceInput.addEventListener("input",updateSlugField)};